b:if.script:
var numberOnPage = '';
//
function numberOfPosts(json) {       
  for (var i = 0; i < json.feed.entry.length; i++) {       
    var numberOnBlog = json.feed.openSearch$totalResults.$t;       
  }  
}
var blogPager = document.querySelector('.blog-pager');
if (numberOnPage <= 25) {
  blogPager.setAttribute('style', 'display: none;');
} else if (25 < numberOnPage <= numberOnBlog) {
  blogPager.setAttribute('style', 'display: block;');
}
