(function() {
  window.addEventListener('load', function() {
    setInterval(function() {
      var parent = document.querySelector('#text-slider ul');
      var slide = parent.querySelectorAll('#text-slider ul li');
      for (var i = 0; i < slide.length; i+= 1) {
        var x = slide[i];
        x.classList.toggle('sliding-now');
      }
      setTimeout(function() {
        parent.appendChild(slide[0]);
      }, 5000);
    }, 5000);
  });
})();
